CREATE TABLE IF NOT EXISTS `user` ( `ID` INT(6) UNSIGNED AUTO_INCREMENT PRIMARY KEY, `UserName` VARCHAR(30) NOT NULL , `Email` VARCHAR(50) NOT NULL , `Password` VARCHAR(10) NOT NULL , `Address` VARCHAR(500) NULL , `ContactNo` BIGINT(15) NULL , `CreatedDate` DATETIME NOT NULL );
CREATE TABLE IF NOT EXISTS `tokens` (`id` INT(6) UNSIGNED AUTO_INCREMENT PRIMARY KEY, `user_id` INT(6) , `token` VARCHAR(50)  );
